INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `comment`) VALUES
(86535, 0, 0, 'Come to free that pitiful drake, demon hunter?', 12, 0, 100, 0, 0, 0, 93006, 'Overseer Lykill to Player'),
(86535, 1, 0, 'There is plenty of room in my cage for your souls.', 14, 0, 100, 0, 0, 0, 97407, 'Overseer Lykill'),
(86535, 2, 0, 'I WILL return!', 12, 0, 100, 0, 0, 57400, 93273, 'Overseer Lykill to Player'),
(86969, 0, 0, 'I think Jace likes his toys a bit too much.', 12, 0, 100, 0, 0, 0, 108292, 'Demon Hunter to Player'),
(87368, 0, 0, 'Farondis! You traitor! You brought this upon us!', 12, 0, 100, 0, 0, 0, 91614, 'Drowned Magister to Player'),
(87369, 0, 0, 'DIE... FARONDIS....', 14, 0, 100, 0, 0, 0, 91615, 'Unbreathing Soul to Player'),
(88086, 0, 0, 'You dare stand against us?', 12, 0, 100, 0, 0, 52682, 92732, 'Hatecoil Riptail to Player'),
(88086, 1, 0, 'Die, landwalker!', 12, 0, 100, 0, 0, 52682, 97409, 'Hatecoil Riptail to Player'),
(88086, 2, 0, 'Your bones will sharpen my trident.', 12, 0, 100, 0, 0, 52681, 92731, 'Hatecoil Riptail to Drowned Nightwatcher'),
(88087, 0, 0, 'Azshara\'s wrath will drown you.', 12, 0, 100, 0, 0, 52706, 92728, 'Hatecoil Stormcaller to Player'),
(88087, 1, 0, 'For Nazjatar!', 12, 0, 100, 0, 0, 52705, 92730, 'Hatecoil Stormcaller to Player'),
