(90267, 1, 0, 'You rats aren\'t fit to touch the hem of my robe!', 12, 0, 100, 0, 0, 52576, 93181, 'Ael\'Yith'),
(90267, 2, 0, 'I need... more... power!', 12, 0, 100, 0, 0, 52575, 93182, 'Ael\'Yith'),
(90267, 3, 0, 'Yes... YEEESSSSS!', 12, 0, 100, 0, 0, 52577, 93183, 'Ael\'Yith'),
(90267, 4, 0, 'Forget the ley-lines, brothers! We shall feast on the dragons instead!', 14, 0, 100, 0, 0, 63240, 113226, 'Ael\'Yith'),
(90317, 0, 0, 'We must get the wards reignited. Otherwise, I guarantee the Legion will overrun our position and pour into Azsuna.', 12, 0, 100, 0, 0, 0, 88528, 'Jace Darkweaver to Player'),
(90372, 0, 0, 'What in blazes are you doing?', 12, 0, 100, 0, 0, 52823, 92867, 'Runas the Shamed to Player'),
(90372, 1, 0, 'Don\'t you know who I am?', 12, 0, 100, 0, 0, 52826, 92868, 'Runas the Shamed to Player'),
(90372, 2, 0, 'Wait... what is that you\'re holding?', 12, 0, 100, 0, 0, 52840, 93004, 'Runas the Shamed to Player'),
(90372, 3, 0, 'Quite an impressive weapon, my friend!', 12, 0, 100, 0, 0, 52838, 92877, 'Runas the Shamed to Player'),
(90372, 4, 0, 'Very painful to be hit with.', 12, 0, 100, 0, 0, 52831, 92996, 'Runas the Shamed to Player'),
(90372, 5, 0, 'You... you are beginning to ANGER ME!!', 12, 0, 100, 0, 0, 52843, 92997, 'Runas the Shamed to Player'),
(90372, 6, 0, 'I\'LL KILL YOU! I\'LL RIP OUT YOUR HEART!', 12, 0, 100, 0, 0, 52841, 93005, 'Runas the Shamed to Player'),
(90372, 7, 0, 'I\'LL DRINK THE MANA FROM YOUR BROKEN BONES!!', 12, 0, 100, 0, 0, 52829, 93237, 'Runas the Shamed to Player'),
(90372, 8, 0, 'I... I...', 12, 0, 100, 0, 0, 52820, 93238, 'Runas the Shamed to Player'),
(90389, 0, 0, 'No... NO! I still thirst. Give me more!', 12, 0, 100, 0, 0, 0, 93176, 'Ishkaneth to Player'),
(90389, 1, 0, 'The power to destroy you!', 12, 0, 100, 0, 0, 0, 102008, 'Ishkaneth'),
(90389, 2, 0, 'More... I need more!', 12, 0, 100, 0, 0, 0, 102009, 'Ishkaneth'),
(90389, 3, 0, 'So... thirsty...', 12, 0, 100, 0, 0, 0, 0, 'Ishkaneth to Player'), -- BroadcastTextID: 46647 - 68570
(90390, 0, 0, 'Kneel, lowborn scum!', 12, 0, 100, 0, 0, 0, 93178, 'Tyndrissen to Player'),
